import React, {  Fragment, useState } from "react";

function Numpad(props) {

    const [newCalc, setNewCalc] = useState(false);

    const handleClick = async e => {

        e.preventDefault();
        try {
            switch (props.value) {
                case '=':
                    calculate();
                    break;
                case 'Cl':
                    clearField();
                    break;
                case 'M+':
                    memoryAdd();
                    break;
                case 'M-':
                    memoryDelete();
                    break;
                case 'MR':
                    memoryRead();
                default:
                    foo();
                    break;
            }
            if(props.value === '=' && !newCalc){
                const operationField = document.getElementById('operation');
                const operation = operationField.value;
                const body = {operation};
                console.log(body);
                const response = await fetch('/api/calculate', {
                    method: "POST",
                    headers: {"Content-Type": "application/json"},
                    body: JSON.stringify(body)
                }).then(res => res.json());
                console.log('response:', response);
                operationField.value = response;
            }else{
                const operation = document.getElementById('operation');
                operation.value += props.value;
            }
        } catch (err) {
            console.error(err.message);
        }
    }

    return (
        <Fragment>
            <div>
                <button className="btn btn-dark btn-block mt-1" onClick={handleClick}>{props.value}</button>
            </div>
        </Fragment>
    );
  }
  
  export default Numpad;
